CREATE DATABASE IF NOT EXISTS dogdb;

USE dogdb;
DROP TABLE IF EXISTS breeds;

CREATE TABLE breeds(
  breed_name varchar(34),
  descript varchar(2235),
  temperament varchar(55),
  popularity varchar(3), 
  min_height varchar(18),
  max_height varchar(18),
  min_weight varchar(18),
  max_weight varchar(18),
  min_expectancy float,
  max_expectancy float,
  dog_group varchar(24),
  grooming_frequency_value float,
  grooming_frequency_category varchar(25),
  shedding_value float,
  shedding_category varchar(10),
  energy_level_value float,
  energy_level_category varchar(22),
  trainability_value float,
  trainability_category varchar(15),
  demeanor_value float,
  demeanor_category varchar(23)
);



DROP TABLE IF EXISTS breeds_webscraped;

CREATE TABLE breeds_webscraped(
  breed_name varchar(34),
  descript_webscraped varchar(1960),
  temperament_webscraped varchar(1504)
);



SELECT breeds.breed_name, breeds.descript, breeds_webscraped.descript_webscraped, 
breeds.temperament, breeds_webscraped.temperament_webscraped, breeds.popularity, 
breeds.min_height, breeds.max_height, breeds.min_weight, breeds.max_weight, 
breeds.min_expectancy, breeds.min_expectancy, breeds.max_expectancy, 
breeds.dog_group, breeds.grooming_frequency_value, breeds.grooming_frequency_category,
breeds.shedding_value, breeds.shedding_category, breeds.energy_level_value,
breeds.energy_level_category, breeds.trainability_value, breeds.trainability_category,
breeds.demeanor_value, breeds.demeanor_category

FROM breeds
INNER JOIN breeds_webscraped ON breeds.breed_name=breeds_webscraped.breed_name;